import React, { useState, useEffect } from "react";

const ShapeScreen = ({ location: { state } }) => {
  const [shapeImage, setShapeImage] = useState("");

  function handleShapeImage() {
    switch (state.shapeName) {
      case "amenazas":
        setShapeImage("amenazas.png");
        break;
      case "infraestructura_critica":
        setShapeImage("infraestructura_critica.png");
        break;
      case "datos_poblacion":
        setShapeImage("datos_poblacion.png");
        break;
      case "variables_fisicas_ambientales":
        setShapeImage("variables_fisicas_ambientales.png");
        break;
      default:
        setShapeImage("");
        break;
    }
  }

  useEffect(() => {
    handleShapeImage(state.shapeName);
  }, []);

  return (
    <div className="container shape-list-container">
      <div className="row">
        <div className="col-md-4">
          <img
            src={`${process.env.PUBLIC_URL}/images/${shapeImage}`}
            width="307"
            height="312"
            alt={state.shapeName}
            loading="lazy"
          />
        </div>
        <div className="col-md-8"></div>
      </div>
    </div>
  );
};

export default ShapeScreen;
